mapper.select:
SELECT COUNT(*) from abandonedAnimal
WHERE TO_CHAR(abani_regdate, 'YYYY-MM-DD')>=#{sD} and TO_CHAR(abani_regdate, 'YYYY-MM-DD')<=#{eD}
WHERE abani_gender like '%'||#{gender}||'%' and abani_species like '%'||#{species}||'%'

  		SELECT * FROM (
  			SELECT Tb.*, rownum rNum FROM (
  				SELECT * FROM abandonedAnimal 


					WHERE
						TO_CHAR(abani_regdate, 'YYYY-MM-DD')>=#{sD} 
		    		and 
		    			TO_CHAR(abani_regdate, 'YYYY-MM-DD')<=#{eD}



					WHERE 
						abani_gender like '%'||#{gender}||'%' 
					and 
						abani_species like '%'||#{species}||'%'

  				ORDER BY abani_idx DESC 
  			) Tb
  		)
  		WHERE rNum>=#{start} AND rNum<=#{end}

